import logo from "./logo.svg";
import "./App.css";
import { useEffect, useState } from "react";

function App() {
	// meme state
	const [memes, setMemes] = useState([]);
	const [selectedMeme, setSelectMeme] = useState({});
	const [caption1, setCaption1] = useState("");
	const [caption2, setCaption2] = useState("");

	useEffect(() => {
		//fetch memes list
		fetch("https://api.imgflip.com/get_memes")
			.then((res) => {
				return res.json();
			})
			.then((resJSON) => setMemes(resJSON.data.memes))
			.catch((err) => console.log(err));
	});

	function selectMemeTemplate(memeObj) {
		setSelectMeme(memeObj);
	}

	function generateMeme() {
		console.log(caption1, caption2);
		// fetch("https://api.imgflip.com/caption_image", {
		// 	method: "POST",
		// 	body: {
		// 		template_id: selectedMeme.id,
		// 		username: "AritraBasu",
		// 		password: "aritrabasu",
		// 		text0: "",
		// 		text1: "",
		// 	},
		// });
	}
	return (
		<div className='App'>
			<div className='selected-meme-container'>
				<div className='selected-meme-container-left'>
					<form>
						<label>Caption 1</label>
						<input
							type='text'
							onChange={(e) => setCaption1(e.target.value)}></input>
						<label>Caption 2</label>
						<input
							type='text'
							onChange={(e) => setCaption2(e.target.value)}></input>
						<button onClick={generateMeme}> Generate Meme </button>
					</form>
				</div>
				<div className='selected-meme-container-right'>
					{selectedMeme && selectedMeme?.id && (
						<img src={selectedMeme.url} className='selected-image' />
					)}
				</div>
			</div>
			<div className='meme-container'>
				{memes.map((meme, index) => {
					return (
						<img
							key={index}
							src={meme.url}
							alt='memeImage'
							style={{ height: "150px", margin: "40px" }}
							onClick={() => selectMemeTemplate(meme)}
						/>
					);
				})}
			</div>
		</div>
	);
}

export default App;
