import logo from "./logo.svg";
import "./App.css";
import { useEffect, useState } from "react";

function App() {
	// meme state
	const [memes, setMemes] = useState([]);
	const [selectedMeme, setSelectMeme] = useState({});

	useEffect(() => {
		//fetch memes list
		fetch("https://api.imgflip.com/get_memes")
			.then((res) => {
				return res.json();
			})
			.then((resJSON) => setMemes(resJSON.data.memes))
			.catch((err) => console.log(err));
	});

	function selectMemeTemplate(memeObj) {
		setSelectMeme(memeObj);
	}
	return (
		<div className='App'>
			{selectedMeme && selectedMeme?.id && <img src={selectedMeme.url} />}
			{memes.map((meme, index) => {
				return (
					<img
						key={index}
						src={meme.url}
						alt='memeImage'
						style={{ height: "150px", margin: "40px" }}
						onClick={() => selectMemeTemplate(meme)}
					/>
				);
			})}
		</div>
	);
}

export default App;
